import React, { useState } from 'react';
import { Button, Grid, Select, InputLabel, Typography, FormControl, MenuItem } from '@material-ui/core';

/* var test = {
    'name': {
        'vodka': 5,
        'orange': 10
    }
} */

class ConfigureScreen extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            'age': 5
        }
    }

    handleChange(props) {

        console.log(props)
    }

    render() {
        return <Grid container>
            <Grid item xs={12} style={{ padding: '10px', height: '20vh' }}>
                <Grid container>
                    <Grid item xs={12} style={{ padding: '10px', height: '20vh', display:'inline-block' }}>

                        <Typography variant="h6">
                            Ingredient: 
                    </Typography>
                        <FormControl>
                            <InputLabel id="demo-simple-select-label">Age</InputLabel>
                            <Select
                                labelId="demo-simple-select-label"
                                id="demo-simple-select"
                                value={this.state.age}
                                onChange={this.handleChange}
                            >
                                <MenuItem value={10}>Ten</MenuItem>
                                <MenuItem value={20}>Twenty</MenuItem>
                                <MenuItem value={30}>Thirty</MenuItem>
                            </Select>
                        </FormControl>
                    </Grid>
                </Grid>
            </Grid>
        </Grid>
    }

}

export default ConfigureScreen
