import React, { useState } from 'react';
import { TextField, Grid, Typography, Button, Box } from '@material-ui/core';
import Autocomplete from '@material-ui/lab/Autocomplete';
import drinksDict from './drinks.json'
import ingredients from './ingredients.json'
import GetDrinkScreen, { GetCustomDrinkScreen } from './GetDrinkScreen'

export default function ChooseDrinkScreen() {
    const [drink, setDrink] = useState(null);

    const [search, setSearch] = useState('')
    const handleSearchChange = (e) => {
        setSearch(e.target.value)
    }

    const [filterIngredients, setFilterIngredients] = useState('')
    const handleIngredientChange = (e, value) => {
        console.log(value)
        setFilterIngredients(e, value)
    }

    if (drink === 'Custom') {
        return <GetCustomDrinkScreen />
    } else if (drink != null) {
        return <GetDrinkScreen drink={capitalize(drink)} qr={`g${drink}`} allowFavorite={true} />
    }
    else {
        return <div style={{ width: '100vw' }}>
            <TextField id="standard-basic" label="Search" onChange={handleSearchChange}
                style={{ width: '95vw' }} />
            <Autocomplete
                multiple
                id="tags-standard"
                options={ingredients}
                getOptionLabel={(option) => capitalize(option)}
                defaultValue={[]}
                onChange={handleIngredientChange}
                renderInput={(params) => (
                    <TextField
                        {...params}
                        variant="standard"
                        label="Ingredients"
                    />
                )}
            />
            <Grid container>
                {
                    search != '' || filterIngredients.length == 0 ?
                        <>
                            <Grid item xs={6} style={{ padding: '10px', height: '25vh' }}>
                                <Button variant="contained" color="primary"
                                    onClick={() => setDrink('Custom')}
                                    style={{ width: '100%', height: '100%' }}>
                                    Custom Drink
                            </Button>
                            </Grid>
                        {Object.keys(drinksDict).map(drink => <DrinkButton drinkName={drink} setDrink={setDrink} />)}
                        </>
                        :
                        Object.keys(drinksDict).map(drink => <DrinkButton drinkName={drink} setDrink={setDrink} />)
                }
            </Grid>

        </div >
    }
}

function filterDrinks(search, ingredients) {
    console.log(search, ingredients)
}

function capitalize(text) {
    return text.toLowerCase()
        .split(' ')
        .map((s) => s.charAt(0).toUpperCase() + s.substring(1))
        .join(' ');
}

function DrinkButton({ drinkName, setDrink }) {
    return <Grid item xs={6} style={{ padding: '10px', height: '25vh' }}>
        <Button variant="contained" color="primary"
            onClick={() => setDrink(drinkName)}
            style={{ width: '100%', height: '100%' }}>
            <div>
                <div>
                    {drinkName}
                </div>
                <br />
                <Box fontStyle='italic'>
                    <Typography variant="caption" style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                        {
                            Object.keys(drinksDict[drinkName]).map(drink => capitalize(drink)).join(', ')
                        }
                    </Typography>
                </Box>
            </div>
        </Button>
    </Grid>
}
