import React, { useState } from 'react';
import { TextField, Grid, Typography, Button } from '@material-ui/core';
import drinksDict from './drinks.json'


export default function ChooseDrinkScreen() {
    const [drink, setDrink] = setState('');
    console.log(drinksDict)
    var QRCode = require('qrcode.react')
    return <div style={{ width: '100vw' }}>
        <Grid container>
            {Object.keys(drinksDict).map(drink => <DrinkButton drinkName={drink} />)}
        </Grid>
    </div>
}

function DrinkButton({drinkName}) {
    return <Grid item xs={6} style={{ padding: '10px', height: '25vh' }}>
        <Button variant="contained" color="primary"
            style={{ width: '100%', height: '100%' }}>
            {drinkName}
        </Button>
    </Grid>
}
