import React, { useState } from 'react';
import { Slider, Grid, Select, InputLabel, Typography, FormControl, MenuItem, Button, Paper } from '@material-ui/core';

/* var test = {
    'name': {
        'vodka': 5,
        'orange': 10
    }
} */

class ConfigureScreen extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            'name': '',
            'ingredients': 1,
            'ingredient0': '',
            'quantity0': 0,
            'ingredient1': '',
            'quantity1': 0,
            'ingredient2': '',
            'quantity2': 0,
            'ingredient3': '',
            'quantity3': 0,
            'ingredient4': '',
            'quantity4': 0,
            'ingredient5': '',
            'quantity5': 0
        }
    }

    handleChange(event, index) {
        /* this.setState({
            [ingredients[index][event.target.name]]: event.target.value
        }) */
    }

    makeIngredient(index, handleChange) {
        console.log('here')
        return <Grid item xs={12} style={{ padding: '10px', height: '20vh' }}>
            <Paper elevation={3} >
                <Typography variant="h6" style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                    Ingredient {index}
                    </Typography>

                <div>
                    <FormControl style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                        {/* <InputLabel id="demo-simple-select-label">Ingredient</InputLabel> */}
                        <Typography id="continuous-slider" gutterBottom>
                            Ingredient
                    </Typography>
                        <Select
                            name={`ingredient${index}`}
                            labelId="demo-simple-select-label"
                            id="demo-simple-select"
                            value={this.state[`ingredient${index}`]}
                            onChange={handleChange}
                            autoWidth
                        >
                            <MenuItem value={10}>Ten</MenuItem>
                            <MenuItem value={20}>Twenty</MenuItem>
                            <MenuItem value={30}>Thirty</MenuItem>
                        </Select>
                    </FormControl>
                </div>
                <br />
                <div>
                    <FormControl style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                        <Typography id="continuous-slider" gutterBottom>
                            Quantity
                    </Typography>
                        <Slider
                            name={`quantity${index}`}
                            defaultValue={20}
                            getAriaValueText={this.state[`quantity${index}`]}
                            aria-labelledby="discrete-slider-custom"
                            valueLabelDisplay="auto"
                            style={{ width: '75%' }}
                        />
                    </FormControl>
                </div>
            </Paper>
        </Grid>
    }

    render() {
        const handleChange = this.handleChange.bind(this);
        console.log(this.state)

        return <Grid container>
            {[...Array(this.state.ingredients).keys()].map(index => {
                this.makeIngredient(index, handleChange)
            })}
            
            <Button variant="contained" color="primary">
                Add
                </Button>
            <Button variant="contained" color="primary">
                Remove
                </Button>
        </Grid>
    }
}

export default ConfigureScreen
