import React, { useState } from 'react';
import { Slider, Grid, Select, InputLabel, Typography, FormControl, MenuItem } from '@material-ui/core';

/* var test = {
    'name': {
        'vodka': 5,
        'orange': 10
    }
} */

class ConfigureScreen extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            'age': '',
            'slider': 0
        }
    }

    handleChange(event) {
        console.log(event)
        this.state[event.target.name] = event.target.value
        console.log(this.state)
    }

    render() {
        const handleChange = this.handleChange.bind(this);
        console.log(this.state)
        return <Grid container>
            <Grid item xs={12} style={{ padding: '10px', height: '20vh' }}>
                <Grid container>
                    <Grid item xs={12} style={{ padding: '10px', height: '20vh', display: 'inline-block' }}>
                        <Typography variant="h6" style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                            Ingredient 1
                            </Typography>

                        <div style={{ display: 'inline-block' }}>
                            <FormControl>
                                <InputLabel id="demo-simple-select-label">Age</InputLabel>
                                <Select
                                    name='age'
                                    labelId="demo-simple-select-label"
                                    id="demo-simple-select"
                                    value={this.state.age}
                                    onChange={handleChange}
                                >
                                    <MenuItem value={10}>Ten</MenuItem>
                                    <MenuItem value={20}>Twenty</MenuItem>
                                    <MenuItem value={30}>Thirty</MenuItem>
                                </Select>
                            </FormControl>
                        </div>
                        <div style={{ display: 'inline-block', width:'50%' }}>
                            <Slider
                                defaultValue={20}
                                getAriaValueText={this.state.slider}
                                aria-labelledby="discrete-slider-custom"
                                step={10}
                                valueLabelDisplay="auto"
                            />
                        </div>
                    </Grid>
                </Grid>
            </Grid>
        </Grid>
    }

}

export default ConfigureScreen
